{{box cssClass="floatinginfobox" title="**Slides**"}}{{groovy}}
     import org.xwiki.rendering.block.match.*
     import org.xwiki.rendering.block.*
     import org.xwiki.rendering.transformation.*
     import org.xwiki.rendering.syntax.*
     import org.xwiki.rendering.listener.*
     import com.xpn.xwiki.web.*

     def xdom = doc.getDocument().getXDOM()
     xdom.getBlocks(new ClassBlockMatcher(HeaderBlock.class), Block.Axes.DESCENDANT).each() { headerBlock ->
        println "* [[${services.rendering.render(new XDOM(headerBlock.getChildren()), "plain/1.0")}>>#${headerBlock.getId()}]]"
     }
     return ""
   {{/groovy}}
{{/box}}

