xwikidoc.content:
{{groovy}}
import org.xwiki.rendering.block.match.*
import org.xwiki.rendering.block.*
import org.xwiki.rendering.transformation.*
import org.xwiki.rendering.syntax.*
import com.xpn.xwiki.web.*

// Find all sections and create one <article> per section
def xdom = xwiki.getDocument(xcontext.get("presentationDocument")).getDocument().getXDOM()
def newxdom = xdom.clone();
def txContext = new TransformationContext(newxdom, Syntax.XWIKI_2_0)
Utils.getComponent(TransformationManager.class).performTransformations(newxdom, txContext)
newxdom.getBlocks(new ClassBlockMatcher(HeaderBlock.class), Block.Axes.DESCENDANT).each() {
  println ""
  println "{{html clean='false'}}<article>"
  // Render the content in XHTML
  print "${services.rendering.render(it, "xhtml/1.0")}"
  print "${services.rendering.render(new XDOM(it.getBlocks(new AnyBlockMatcher(), Block.Axes.FOLLOWING_SIBLING)), "xhtml/1.0")}"
  println "</article>{{/html}}"
  println ""
}
print ""
 {{/groovy}}
